insert or replace into LocalizedText
	(Language,		Tag,																Text)
values
	("en_US",		"LOC_UNIT_NORWEGIAN_ULFHEDNAR_RA_DESCRIPTION",						"Norwegian unique ancient era Religious Arm unit. Can be trained or purchased without a major religion. +50% Experience for adjacent units.{LOC_UNIT_HD_RELIGIOUS_ARM_COMMON}"),
	("zh_Hans_CN",	"LOC_UNIT_NORWEGIAN_ULFHEDNAR_RA_DESCRIPTION",						"挪威远古时代特色宗教武装单位，不需要主流宗教就可以训练或购买。为相邻单位提供+50%经验值。{LOC_UNIT_HD_RELIGIOUS_ARM_COMMON}"),
	("en_US",		"LOC_UNIT_JAPANESE_SOHEI_RA_DESCRIPTION",							"Japanese unique Religious Arm that replaces the Expeditionary. May be purchased with [ICON_GOLD] Gold. Cost 3 [ICON_FAITH] Faith to maintain.{LOC_UNIT_HD_RELIGIOUS_ARM_COMMON}"),
	("zh_Hans_CN",	"LOC_UNIT_JAPANESE_SOHEI_RA_DESCRIPTION",							"日本特色宗教武装单位，取代远征军。可以用 [ICON_GOLD] 金币购买。消耗3 [ICON_FAITH] 信仰维护费。{LOC_UNIT_HD_RELIGIOUS_ARM_COMMON}"),
	("en_US",		"LOC_UNIT_ELEANOR_TEMPLAR_RA_DESCRIPTION",							"Unique Medieval Riligious Arm unit for Eleanor of Aquitaine. +5 [ICON_STRENGTH] Combat Strength when opponent is of another religion. +5 [ICON_STRENGTH] Combat Strength when adjacent to a Holy Site District. Blocks the first non-lethal attack each turn. Cost 3 [ICON_FAITH] Faith to maintain.{LOC_UNIT_HD_RELIGIOUS_ARM_COMMON}"),
	("zh_Hans_CN",	"LOC_UNIT_ELEANOR_TEMPLAR_RA_DESCRIPTION",							"阿基坦埃莉诺中世纪特色宗教武装单位。对抗异教徒时+5 [ICON_STRENGTH] 战斗力，相邻圣地时+5 [ICON_STRENGTH] 战斗力。格挡每回合受到的第一次非致命攻击。消耗3 [ICON_FAITH] 信仰维护费。{LOC_UNIT_HD_RELIGIOUS_ARM_COMMON}"),
	("en_US",		"LOC_UNIT_INDONESIAN_KRIS_SWORDSMAN_RA_DESCRIPTION",				"Indonesian unique Riligious Arm unit that replaces the Armed Believer. May be purchased with [ICON_GOLD] Gold. Cost 2 [ICON_FAITH] Faith to maintain.{LOC_UNIT_HD_RELIGIOUS_ARM_COMMON}"),
	("zh_Hans_CN",	"LOC_UNIT_INDONESIAN_KRIS_SWORDSMAN_RA_DESCRIPTION",				"印度尼西亚特色宗教武装单位，取代武装教徒。可以用 [ICON_GOLD] 金币购买。消耗2 [ICON_FAITH] 信仰维护费。{LOC_UNIT_HD_RELIGIOUS_ARM_COMMON}"),
	("en_US",		"LOC_LEADER_TRAIT_LAHORE_DESCRIPTION",								"Unlocks the Lahore City-State unique Riligious Arm unit Nihang. +15 [ICON_STRENGTH] Combat Strength for all Nihang units."),
	("zh_Hans_CN",	"LOC_LEADER_TRAIT_LAHORE_DESCRIPTION",								"解锁拉合尔城邦特色宗教武装单位尼杭战士。所有尼杭战士+15 [ICON_STRENGTH] 战斗力。"),
	("en_US",		"LOC_LEADER_TRAIT_JERUSALEM_DESCRIPTION_EXPANSION",					"Upon conquering a city with Religious Arm unit or with Religious Arm unit adjacent to the city center, your majority religion becomes the dominant religion in that city."),
	("zh_Hans_CN",	"LOC_LEADER_TRAIT_JERUSALEM_DESCRIPTION_EXPANSION",					"用宗教武装单位攻占城市或攻占城市时有宗教武装单位与城市中心相邻，您的主流宗教成为该城市的主流宗教。"),
	("en_US",		"LOC_IMPROVEMENT_MONASTERY_EXPANSION2_DESCRIPTION",					"Unlocks the Builder ability to construct a Monastery.[NEWLINE][NEWLINE]+2 [ICON_FAITH] Faith, +1 [ICON_HOUSING] Housing. +2 [ICON_GOLD] Gold for every adjacent District. Religious Arm unit trained in cities with a Monastery gain +50% experience."),
	("zh_Hans_CN",	"LOC_IMPROVEMENT_MONASTERY_EXPANSION2_DESCRIPTION",					"解锁建造者建造修道院的能力。[NEWLINE][NEWLINE]+2 [ICON_FAITH] 信仰值，+1 [ICON_HOUSING] 住房。每相邻1个区域+2 [ICON_GOLD] 金币。有修道院的城市训练的宗教武装单位+50%经验。");