insert or replace into LocalizedText
    (Language,      Tag,                                                     Text)
values
    ("zh_Hans_CN",  "LOC_MOMENT_CATEGORY_SCIENTIFIC_BONUS_GOLDEN_AGE",      "“自由探索”黄金时代：[NEWLINE][ICON_CivicBoosted]鼓舞额外提供10%的科技开销。城市每拥有1处特色区域，则+2 [ICON_Science] 科技值。港口提供等同于金币的科技。"),
    ("zh_Hans_CN",  "LOC_DISTRICT_CULTURE_MBANZA",                          "+2 [ICON_Culture] 文化值来自相邻姆班赞。"),
    ("zh_Hans_CN",  "LOC_DISTRICT_GOLD_MBANZA",                             "+2 [ICON_Gold] 金币来自相邻姆班赞。"),
    ("zh_Hans_CN",  "LOC_DISTRICT_MBANZA_HD_DESCRIPTION",                   "刚果特色区域，只能建在雨林或树林里。替代社区，可更早建造，且建造费用更低。无视魅力值，固定提供+5 [ICON_Housing] 住房、+3 [ICON_Food] 食物和+5 [ICON_Gold] 金币。为相邻地块+1魅力，为剧院商业提供大加成。允许使用 [ICON_Faith] 信仰值购买开拓者和建造者。"),
    ("zh_Hans_CN",  "LOC_TRAIT_CIVILIZATION_NKISI_DESCRIPTION",             "所有陆地单位无视森林和雨林对移动力的减益。所有巨作除了通常的文化值，还会从每个肖像，风景，宗教，遗物，文物以及雕塑艺术巨作中获得+3 [ICON_Food] 食物、+3 [ICON_Production] 生产力、+6 [ICON_Gold] 金币和+3 [ICON_Faith] 信仰值，书提供+2 [ICON_Food] 食物、+2 [ICON_Production] 生产力、+3 [ICON_Gold] 金币和+2 [ICON_Faith] 信仰值。获得额外100%大作家、大艺术家、大音乐家和大商人点数。宫殿有5个巨作槽位。"),
    ("zh_Hans_CN",  "LOC_TRAIT_LEADER_RELIGIOUS_CONVERT_DESCRIPTION",       "无法建造圣地、获得大预言家，或创建宗教。从已成为多数刚果城市信仰的宗教中获得信仰值。每次完成一个姆班赞或剧院广场时获得一位（城市主流宗教的）使徒。剧院提供等同于相邻文化数值的信仰。"),
    ("zh_Hans_CN",  "LOC_BELIEF_DAR_E_MEHR_DL_DESCRIPTION",                 "允许建造拜火神庙（+2 [ICON_Faith] 信仰值；建造或上次修复后的每个时代均额外+2 [ICON_FAITH] 信仰值；完成后，获得一个传教士和一个武僧）。 "),
    ("zh_Hans_CN",  "LOC_BELIEF_SYNAGOGUE_DL_DESCRIPTION",                  "允许建造犹太教堂（+8 [ICON_Faith] 信仰值；本城+15% [ICON_FAITH] 信仰值）。"),
    ("zh_Hans_CN",  "LOC_BUILDING_SYNAGOGUE_DL_DESCRIPTION",                "本城+15% [ICON_FAITH] 信仰。"),
    ("zh_Hans_CN",  "LOC_BELIEF_TITHE_DL_DESCRIPTION",                      "该宗教每有1个信徒则+2 [ICON_Gold] 金币。"),
    ("zh_Hans_CN",  "LOC_BELIEF_RELIGIOUS_UNITY_DL_DESCRIPTION",            "该宗教每有1个信徒则+1 [ICON_Faith] 信仰。"),
    ("zh_Hans_CN",  "LOC_BELIEF_PILGRIMAGE_DL_DESCRIPTION",                 "每个信奉该宗教的城市+6 [ICON_Faith] 信仰。"),
    ("zh_Hans_CN",  "LOC_BELIEF_ZEN_MEDITATION_DL_DESCRIPTION",             "若圣地相邻山脉，+1 [ICON_Amenities] 宜居度；相邻森林，+1 [ICON_Amenities] 宜居度；相邻河流，+1 [ICON_housing] 住房，+2 [ICON_culture] 文化。市中心也获得相同加成。"),
    ("zh_Hans_CN",  "LOC_BELIEF_MESSIAH_DL_DESCRIPTION",                    "购买圣地区域内建筑的费用降低20%且可以用 [ICON_Faith] 信仰值购买。每个圣地区域内的建筑提供+8% [ICON_Faith] 信仰值。"),
    ("zh_Hans_CN",  "LOC_BELIEF_JESUIT_EDUCATION_DL_DESCRIPTION",           "可以使用 [ICON_Faith] 信仰值购买学院区域内的建筑。每人口提供0.2 [ICON_Science] 科技值，若城市有神社则变为0.3，有寺庙则变为0.5。");
