    ("zh_Hans_CN",  "LOC_CITY_NAME_FRANCE_GEDEMO_AIXLACHAPELLE",                                     "亚琛"),
    ("zh_Hans_CN",  "LOC_TRAIT_LEADER_GEDEMO_CHARLEMAGNE1_NAME",                                     "皇家信使"),
