    ("zh_Hans_CN",  "LOC_CIVINFO_SUK_ICELAND_LOCATION",                                                                                      "欧洲"),
    ("zh_Hans_CN",  "LOC_CIVINFO_SUK_ICELAND_SIZE",                                                                                          "约39.7万平方英里（10.27万平方公里）"),
